import React from "react";
import styled from "styled-components";
import { Root, Trigger, List, Option, Portal  } from "../components/select/Select";

const FeatureSelect = () => {
  return (
    <div id="one">
      <Container>
        <h1>Select </h1>
        <div style={{ display: "flex", gap: "10px" }}>
          <Root>
            <Trigger>트리거</Trigger>
            <Portal>
              <List>
                <Option value="1">리액트</Option>
                <Option value="2">자바</Option>
                <Option value="3">스프링</Option>
                <Option value="4">리액트네이티브</Option>
              </List>
            </Portal>
          </Root>

          <Select.Root>
            <Trigger>트리거</Trigger>
            <List>
              <Option value="1">리액트</Option>
              <Option value="2">자바</Option>
              <Option value="3">스프링</Option>
              <Option value="4">리액트네이티브</Option>
            </List>
          </Select.Root>
        </div>
      </Container>
    </div>
  );
};

export default FeatureSelect;

const SelectTrigger = styled(Select.Trigger)`
  border: 1px solid #ddd;
  height: 40px;
  width: 300px;
  background-color: #fff;
  border-radius: 12px;
`;

const OverSelectList = styled(Select.List)`
  border: 1px solid #eee;
  border-radius: 12px;
  z-index: 2;
  background-color: #fff;
  width: 300px;
  position: absolute;
  top: 650px;
`;

const SelectList = styled(Select.List)`
  border: 1px solid #eee;
  border-radius: 12px;
  z-index: 2;
  background-color: #fff;
  width: 300px;
  position: absolute;
  top: 50px;
`;

const SelectOption = styled(Select.Option)`
  font-size: 12px;
  display: flex;
  align-items: center;
  padding-left: 12px;
  height: 40px;

  :hover {
    background-color: #eee;
  }

  :first-child {
    border-top-left-radius: 12px;
    border-top-right-radius: 12px;
  }

  :last-child {
    border-bottom-left-radius: 12px;
    border-bottom-right-radius: 12px;
  }
`;

const Container = styled.div`
  border: 3px solid #ddd;
  height: 200px;
  overflow: hidden;
  position: relative;
  margin-top: 50px;
`;
