import { useEffect, useState } from "react";
import "./App.css";
import Modal from "./components/Modal/Modal";

function App() {
  const [products, setProducts] = useState();

  useEffect(() => {
    // 1초 후, 상품 초기화
     setTimeout(() => {
       setProducts(mockTheme1Produdcts);
     }, 1000);
 }, []);

  const onClickThemeButton = (themeId) => {
    if (themeId === 1) {
        setProducts(mockTheme1Produdcts);
    } else {
        setProducts(mockTheme2Produdcts);
    }
 };

 products.map((product) => (
  <button
        onClick={() => navigate(`product/${product.id}`)}
        key={product.id}
        name={product.name}
        description={product.description}
        thumbnail={product.thumbnail}
/>
))

  const ThemeButton = ({ themeName, onClick }) => {
    return <ThemeButtonStyled onClick={onClick}>{themeName}</ThemeButtonStyled>;
  };
  return (
    <div>
      <select>
        {products ? (
          products.map((product) => (
            <ProductCard
              onClick={() => navigate(`product/${product.id}`)}
              key={product.id}
              name={product.name}
              description={product.description}
              thumbnail={product.thumbnail}
            />
          ))
        ) : (
          <div>테마를 선택해주세요</div>
        )}
      </select>
      <select>
        <ThemeButton
          themeName={"#따순머그컵"}
          onClick={() => onClickThemeButton(1)}
        />
        <ThemeButton
          themeName={"#여름엔바로"}
          onClick={() => onClickThemeButton(2)}
        />
      </select>
      <Modal />
    </div>
  );
}

export default App;
